CREATE TABLE `causa` (`id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY, `created` DATETIME NULL DEFAULT NULL, `modified` DATETIME NULL DEFAULT NULL, `lat` VARCHAR(30) NOT NULL, `long` VARCHAR(30) NOT NULL, `titulo` VARCHAR(200) NOT NULL, `contenido` TEXT NOT NULL, `direccion` VARCHAR(300) NOT NULL, `telefono` VARCHAR(100) NOT NULL, `email` VARCHAR(100) NOT NULL, `necesidad` VARCHAR(300) NOT NULL, `categoria` INT(3) NOT NULL) ENGINE = InnoDB CHARACTER SET utf8 COLLATE utf8_spanish_ci;